server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { insertUserSchema, insertMessageSchema } from "@shared/schema";
import bcrypt from "bcrypt";
import session from "express-session";
import MemoryStore from "memorystore";

export async function registerRoutes(app: Express): Promise<Server> {
  // Session configuration (Memory Store)
  const MemoryStoreSession = MemoryStore(session);
  app.use(session({
    store: new MemoryStoreSession({
      checkPeriod: 86400000, // prune expired entries every 24h
    }),
    secret: process.env.SESSION_SECRET || "secure-professional-bank-secret",
    resave: false,
    saveUninitialized: false,
    cookie: {
      secure: false, // Set to true in production with HTTPS
      httpOnly: true,
      maxAge: 24 * 60 * 60 * 1000, // 24 hours
    },
  }));

  // Auth middleware
  const requireAuth = (req: any, res: any, next: any) => {
    if (!req.session.userId) {
      return res.status(401).json({ message: "Unauthorized" });
    }
    next();
  };

  const requireAdmin = (req: any, res: any, next: any) => {
    if (!req.session.userId || !req.session.isAdmin) {
      return res.status(403).json({ message: "Admin access required" });
    }
    next();
  };

  // User registration
  app.post("/api/auth/register", async (req, res) => {
    try {
      const validatedData = insertUserSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(validatedData.email);
      if (existingUser) {
        return res.status(400).json({ message: "User already exists" });
      }

      const user = await storage.createUser(validatedData);

      // Create session for the new user
      req.session.userId = user.id;
      req.session.isAdmin = false;
      req.session.isApproved = user.isApproved ?? false;

      res.json({ 
        user: { 
          id: user.id, 
          email: user.email, 
          firstName: user.firstName, 
          lastName: user.lastName,
          isApproved: user.isApproved 
        } 
      });
    } catch (error) {
      res.status(400).json({ message: "Registration failed", error: error instanceof Error ? error.message : "Unknown error" });
    }
  });

  // User login
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { email, password } = req.body;

      const user = await storage.getUserByEmail(email);
      if (!user || user.isAdmin) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const isValidPassword = await bcrypt.compare(password, user.password);
      if (!isValidPassword) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      req.session.userId = user.id;
      req.session.isAdmin = false;
      req.session.isApproved = user.isApproved ?? false;

      res.json({ 
        user: { 
          id: user.id, 
          email: user.email, 
          firstName: user.firstName, 
          lastName: user.lastName,
          isApproved: user.isApproved 
        } 
      });
    } catch (error) {
      res.status(500).json({ message: "Login failed" });
    }
  });

  // Admin login
  app.post("/api/auth/admin-login", async (req, res) => {
    try {
      const { email, username, password } = req.body;

      // Check hardcoded admin credentials
      if (email === "spb@admin.io" && username === "SPB Admin" && password === "SpbAdminLogin@01,.") {
        const admin = await storage.getAdminByCredentials(email, username);
        if (admin) {
          req.session.userId = admin.id;
          req.session.isAdmin = true;
          req.session.isApproved = true;

          res.json({ 
            user: { 
              id: admin.id, 
              email: admin.email, 
              firstName: admin.firstName, 
              lastName: admin.lastName,
              isAdmin: true 
            } 
          });
          return;
        }
      }

      res.status(401).json({ message: "Invalid admin credentials" });
    } catch (error) {
      res.status(500).json({ message: "Admin login failed" });
    }
  });

  // Get current user
  app.get("/api/auth/user", requireAuth, async (req, res) => {
    try {
      const user = await storage.getUser(req.session.userId!);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      res.json({ 
        id: user.id, 
        email: user.email, 
        firstName: user.firstName, 
        lastName: user.lastName,
        isApproved: user.isApproved,
        isAdmin: user.isAdmin 
      });
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Logout
  app.post("/api/auth/logout", (req, res) => {
    req.session.destroy((err) => {
      if (err) {
        return res.status(500).json({ message: "Logout failed" });
      }
      res.json({ message: "Logged out successfully" });
    });
  });

  // Get all users (admin only)
  app.get("/api/admin/users", requireAdmin, async (req, res) => {
    try {
      const users = await storage.getAllUsers();
      res.json(users.map(user => ({
        id: user.id,
        email: user.email,
        firstName: user.firstName,
        lastName: user.lastName,
        isApproved: user.isApproved,
        createdAt: user.createdAt,
        phone: user.phone,
        streetAddress: user.streetAddress,
        city: user.city,
        state: user.state,
        zipCode: user.zipCode,
        ssn: `***-**-${user.ssn.slice(-4)}`, // Mask SSN
      })));
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch users" });
    }
  });

  // Get pending users (admin only)
  app.get("/api/admin/pending-users", requireAdmin, async (req, res) => {
    try {
      const users = await storage.getPendingUsers();
      res.json(users.map(user => ({
        id: user.id,
        email: user.email,
        firstName: user.firstName,
        lastName: user.lastName,
        createdAt: user.createdAt,
        phone: user.phone,
        streetAddress: user.streetAddress,
        city: user.city,
        state: user.state,
        zipCode: user.zipCode,
        ssn: `***-**-${user.ssn.slice(-4)}`,
      })));
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch pending users" });
    }
  });

  // Approve user (admin only)
  app.post("/api/admin/approve-user/:id", requireAdmin, async (req, res) => {
    try {
      const user = await storage.updateUserApproval(req.params.id, true);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Create default checking account for approved user
      await storage.createAccount({
        userId: user.id,
        accountNumber: `SPB${Date.now()}${Math.random().toString(36).substr(2, 4).toUpperCase()}`,
        accountType: "checking",
        balance: "0.00",
        isActive: true,
      });

      res.json({ message: "User approved successfully", user });
    } catch (error) {
      res.status(500).json({ message: "Failed to approve user" });
    }
  });

  // Reject user (admin only)
  app.post("/api/admin/reject-user/:id", requireAdmin, async (req, res) => {
    try {
      const user = await storage.updateUserApproval(req.params.id, false);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json({ message: "User rejected", user });
    } catch (error) {
      res.status(500).json({ message: "Failed to reject user" });
    }
  });

  // Get messages for current user
  app.get("/api/messages", requireAuth, async (req, res) => {
    try {
      const adminId = req.session.isAdmin ? req.session.userId! : "admin-id";
      const userId = req.session.isAdmin ? undefined : req.session.userId!;

      let messages;
      if (req.session.isAdmin) {
        // Admin sees all messages
        messages = await storage.getMessages(adminId);
      } else {
        // User sees messages with admin
        messages = await storage.getMessages(userId!, adminId);
      }

      res.json(messages);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch messages" });
    }
  });

  // Get conversations (admin only)
  app.get("/api/admin/conversations", requireAdmin, async (req, res) => {
    try {
      const conversations = await storage.getConversations(req.session.userId!);
      res.json(conversations);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch conversations" });
    }
  });

  // Send message
  app.post("/api/messages", requireAuth, async (req, res) => {
    try {
      const { content, toUserId } = req.body;

      const message = await storage.createMessage({
        fromUserId: req.session.userId!,
        toUserId: toUserId || (req.session.isAdmin ? undefined : "admin-id"),
        content,
        isFromAdmin: req.session.isAdmin,
      });

      // Broadcast message via WebSocket
      if (wss) {
        const messageData = JSON.stringify({
          type: 'message',
          data: message
        });

        wss.clients.forEach(client => {
          if (client.readyState === WebSocket.OPEN) {
            client.send(messageData);
          }
        });
      }

      res.json(message);
    } catch (error) {
      res.status(500).json({ message: "Failed to send message" });
    }
  });

  // Get user accounts
  app.get("/api/accounts", requireAuth, async (req, res) => {
    try {
      const user = await storage.getUser(req.session.userId!);
      if (!user?.isApproved) {
        return res.status(403).json({ message: "Account not approved" });
      }

      const accounts = await storage.getAccountsByUserId(req.session.userId!);
      res.json(accounts);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch accounts" });
    }
  });

  // Get user transactions
  app.get("/api/transactions", requireAuth, async (req, res) => {
    try {
      const user = await storage.getUser(req.session.userId!);
      if (!user?.isApproved) {
        return res.status(403).json({ message: "Account not approved" });
      }

      const accounts = await storage.getAccountsByUserId(req.session.userId!);
      const allTransactions = [];

      for (const account of accounts) {
        const transactions = await storage.getTransactionsByAccountId(account.id);
        allTransactions.push(...transactions);
      }

      res.json(allTransactions.sort((a, b) => 
        new Date(b.createdAt!).getTime() - new Date(a.createdAt!).getTime()
      ));
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch transactions" });
    }
  });

  // Get all transactions (admin only)
  app.get("/api/admin/transactions", requireAdmin, async (req, res) => {
    try {
      const transactions = await storage.getAllTransactions();
      res.json(transactions);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch transactions" });
    }
  });

  const httpServer = createServer(app);

  // WebSocket server for real-time messaging
  let wss: WebSocketServer;
  wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  wss.on('connection', (ws) => {
    console.log('Client connected to WebSocket');

    ws.on('message', (message) => {
      try {
        const data = JSON.parse(message.toString());
        console.log('Received WebSocket message:', data);

        // Broadcast message to all connected clients
        wss.clients.forEach(client => {
          if (client !== ws && client.readyState === WebSocket.OPEN) {
            client.send(message.toString());
          }
        });
      } catch (error) {
        console.error('Invalid WebSocket message:', error);
      }
    });

    ws.on('close', () => {
      console.log('Client disconnected from WebSocket');
    });
  });

  return httpServer;
}

server/storage.ts.content:
import {
  type User,
  type InsertUser,
  type Message,
  type InsertMessage,
  type Account,
  type InsertAccount,
  type Transaction,
  type InsertTransaction,
  type LoanApplication,
  type InsertLoanApplication,
} from "@shared/schema";
import { connectDB } from "./db";
import bcrypt from "bcrypt";
import { ObjectId } from "mongodb";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(insertUser: InsertUser): Promise<User>;
  updateUserApproval(id: string, isApproved: boolean): Promise<User | undefined>;
  getPendingUsers(): Promise<User[]>;
  getAllUsers(): Promise<User[]>;
  getMessages(userId1: string, userId2?: string): Promise<Message[]>;
  createMessage(message: InsertMessage): Promise<Message>;
  getConversations(adminId: string): Promise<{user: User, lastMessage: Message | null, unreadCount: number}[]>;
  getAccountsByUserId(userId: string): Promise<Account[]>;
  createAccount(account: InsertAccount): Promise<Account>;
  getTransactionsByAccountId(accountId: string): Promise<Transaction[]>;
  createTransaction(transaction: InsertTransaction): Promise<Transaction>;
  getAllTransactions(): Promise<(Transaction & {accountNumber: string, userName: string})[]>;
  getLoanApplicationsByUserId(userId: string): Promise<LoanApplication[]>;
  createLoanApplication(loan: InsertLoanApplication): Promise<LoanApplication>;
  getAllLoanApplications(): Promise<(LoanApplication & {userName: string})[]>;
  getAdminByCredentials(email: string, username: string): Promise<User | undefined>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const db = await connectDB();
    return await db.collection<User>("users").findOne({ id }) || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const db = await connectDB();
    return await db.collection<User>("users").findOne({ email }) || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const db = await connectDB();
    const hashedPassword = await bcrypt.hash(insertUser.password, 10);
    const user: User = {
      ...insertUser,
      password: hashedPassword,
      id: new ObjectId().toString(),
      isApproved: false,
      isAdmin: false,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    await db.collection<User>("users").insertOne(user);
    return user;
  }

  async updateUserApproval(id: string, isApproved: boolean): Promise<User | undefined> {
    const db = await connectDB();
    const result = await db.collection<User>("users").findOneAndUpdate(
      { id },
      { $set: { isApproved, updatedAt: new Date() } },
      { returnDocument: "after" }
    );
    return result || undefined;
  }

  async getPendingUsers(): Promise<User[]> {
    const db = await connectDB();
    return await db.collection<User>("users").find({ isApproved: false, isAdmin: false }).sort({ createdAt: -1 }).toArray();
  }

  async getAllUsers(): Promise<User[]> {
    const db = await connectDB();
    return await db.collection<User>("users").find({ isAdmin: false }).sort({ createdAt: -1 }).toArray();
  }

  async getMessages(userId1: string, userId2?: string): Promise<Message[]> {
    const db = await connectDB();
    if (userId2) {
      return await db.collection<Message>("messages").find({
        $or: [
          { fromUserId: userId1, toUserId: userId2 },
          { fromUserId: userId2, toUserId: userId1 }
        ]
      }).sort({ createdAt: 1 }).toArray();
    } else {
      return await db.collection<Message>("messages").find({
        $or: [
          { fromUserId: userId1 },
          { toUserId: userId1 }
        ]
      }).sort({ createdAt: 1 }).toArray();
    }
  }

  async createMessage(message: InsertMessage): Promise<Message> {
    const db = await connectDB();
    const msg: Message = {
      ...message,
      id: new ObjectId().toString(),
      createdAt: new Date(),
    };
    await db.collection<Message>("messages").insertOne(msg);
    return msg;
  }

  async getConversations(adminId: string): Promise<{user: User, lastMessage: Message | null, unreadCount: number}[]> {
    const allUsers = await this.getAllUsers();
    const conversations: {user: User, lastMessage: Message | null, unreadCount: number}[] = [];
    for (const user of allUsers) {
      const userMessages = await this.getMessages(adminId, user.id);
      const lastMessage = userMessages.length > 0 ? userMessages[userMessages.length - 1] : null;
      const unreadCount = userMessages.filter(msg => msg.fromUserId === user.id && msg.toUserId === adminId).length;
      conversations.push({ user, lastMessage, unreadCount });
    }
    return conversations.sort((a, b) => {
      if (!a.lastMessage && !b.lastMessage) return 0;
      if (!a.lastMessage) return 1;
      if (!b.lastMessage) return -1;
      return new Date(b.lastMessage!.createdAt!).getTime() - new Date(a.lastMessage!.createdAt!).getTime();
    });
  }

  async getAccountsByUserId(userId: string): Promise<Account[]> {
    const db = await connectDB();
    return await db.collection<Account>("accounts").find({ userId }).toArray();
  }

  async createAccount(account: InsertAccount): Promise<Account> {
    const db = await connectDB();
    const accountNumber = `SPB${Date.now()}${Math.random().toString(36).substr(2, 4).toUpperCase()}`;
    const acc: Account = {
      ...account,
      id: new ObjectId().toString(),
      accountNumber,
      isActive: true,
      createdAt: new Date(),
    };
    await db.collection<Account>("accounts").insertOne(acc);
    return acc;
  }

  async getTransactionsByAccountId(accountId: string): Promise<Transaction[]> {
    const db = await connectDB();
    return await db.collection<Transaction>("transactions").find({ accountId }).sort({ createdAt: -1 }).toArray();
  }

  async createTransaction(transaction: InsertTransaction): Promise<Transaction> {
    const db = await connectDB();
    const tx: Transaction = {
      ...transaction,
      id: new ObjectId().toString(),
      status: "completed",
      createdAt: new Date(),
    };
    await db.collection<Transaction>("transactions").insertOne(tx);
    return tx;
  }

  async getAllTransactions(): Promise<(Transaction & {accountNumber: string, userName: string})[]> {
    const db = await connectDB();
    const txs = await db.collection<Transaction>("transactions").find({}).sort({ createdAt: -1 }).toArray();
    const accounts = await db.collection<Account>("accounts").find({}).toArray();
    const users = await db.collection<User>("users").find({}).toArray();
    return txs.map(tx => {
      const acc = accounts.find(a => a.id === tx.accountId);
      const user = users.find(u => u.id === acc?.userId);
      return {
        ...tx,
        accountNumber: acc?.accountNumber || "",
        userName: user ? `${user.firstName} ${user.lastName}`.trim() : ""
      };
    });
  }

  async getLoanApplicationsByUserId(userId: string): Promise<LoanApplication[]> {
    const db = await connectDB();
    return await db.collection<LoanApplication>("loanApplications").find({ userId }).sort({ createdAt: -1 }).toArray();
  }

  async createLoanApplication(loan: InsertLoanApplication): Promise<LoanApplication> {
    const db = await connectDB();
    const loanApp: LoanApplication = {
      ...loan,
      id: new ObjectId().toString(),
      status: "pending",
      createdAt: new Date(),
    };
    await db.collection<LoanApplication>("loanApplications").insertOne(loanApp);
    return loanApp;
  }

  async getAllLoanApplications(): Promise<(LoanApplication & {userName: string})[]> {
    const db = await connectDB();
    const loans = await db.collection<LoanApplication>("loanApplications").find({}).sort({ createdAt: -1 }).toArray();
    const users = await db.collection<User>("users").find({}).toArray();
    return loans.map(loan => {
      const user = users.find(u => u.id === loan.userId);
      return {
        ...loan,
        userName: user ? `${user.firstName} ${user.lastName}`.trim() : ""
      };
    });
  }

  async getAdminByCredentials(email: string, username: string): Promise<User | undefined> {
    // Hardcoded admin credentials as specified
    if (email === "spb@admin.io" && username === "SPB Admin") {
      return {
        id: "admin-id",
        firstName: "SPB",
        lastName: "Admin",
        email: "spb@admin.io",
        password: "",
        ssn: "",
        phone: "",
        streetAddress: "",
        city: "",
        state: "",
        zipCode: "",
        dateOfBirth: new Date(),
        isApproved: true,
        isAdmin: true,
        createdAt: new Date(),
        updatedAt: new Date(),
      };
    }
    return undefined;
  }
}

export const storage = new DatabaseStorage();

